import React, { useEffect, useState } from "react";
import Navbar from "./NavBar";
import "./Home.css";
import { useNavigate } from "react-router-dom";
const Home = () => {
  const navigate = useNavigate();
  const [posts, setposts] = useState([]);
  const [user, setuser] = useState({});
  const [comment, setcomment] = useState("");
  const [item, setitem] = useState({});

  useEffect(() => {
    if (!localStorage.getItem("token")) {
      navigate("/login");
    }
    setposts([]);
    getdetails();
    populatePosts();
    // eslint-disable-next-line
  }, []);

  const getdetails = async () => {
    const res = await fetch("http://localhost:2000/fetchdetails", {
      method: "get",
      headers: {
        "auth-token": localStorage.getItem("token"),
      },
    });
    const parsed = await res.json();
    setuser(parsed.user);
  };

  const populatePosts = async () => {
    const res = await fetch("http://localhost:2000/fetchposts", {
      method: "get",
      headers: {
        "Content-Type": "application/json",
        "auth-token": localStorage.getItem("token"),
      },
    });
    const parsed = await res.json();
    setposts(parsed);
  };

  const likepost = async (postId) => {
    const res = await fetch("http://localhost:2000/like", {
      method: "put",
      headers: {
        "Content-Type": "application/json",
        "auth-token": localStorage.getItem("token"),
      },
      body: JSON.stringify({ postId }),
    });
    const parsed = await res.json();
    if (parsed) populatePosts();
  };
  const unlikepost = async (postId) => {
    const res = await fetch("http://localhost:2000/unlike", {
      method: "put",
      headers: {
        "Content-Type": "application/json",
        "auth-token": localStorage.getItem("token"),
      },
      body: JSON.stringify({ postId }),
    });
    const parsed = await res.json();
    if (parsed) populatePosts();
  };

  const handlecomment = async (postId) => {
    const res = await fetch("http://localhost:2000/comment", {
      method: "put",
      headers: {
        "Content-Type": "application/json",
        "auth-token": localStorage.getItem("token"),
      },
      body: JSON.stringify({ comment, postId }),
    })
    setcomment("")
    populatePosts();
  };
  return (
    <>
      <Navbar populatePosts={populatePosts} />
      <div className="home">
        {posts.map((element) => {
          return (
            <div className="card" key={element.image}>
              <div className="card-header">
                <div className="card-pic">
                  <img
                    src="https://images.pexels.com/photos/15422042/pexels-photo-15422042/free-photo-of-black-and-white-fashion-man-people.jpeg?auto=compress&cs=tinysrgb&w=800"
                    alt=""
                  />
                </div>
                <h3>{element.postedBy.name}</h3>
              </div>
              <div className="card-image">
                <img src={element.image} alt="" />
              </div>
              <div className="card-content">
                {!element.likes.includes(user._id) ? (
                  <span
                    className="material-symbols-outlined"
                    onClick={() => {
                      likepost(element._id);
                    }}
                  >
                    favorite
                  </span>
                ) : (
                  <span
                    className="material-symbols-outlined red"
                    onClick={() => {
                      unlikepost(element._id);
                    }}
                  >
                    favorite
                  </span>
                )}
                <p>{element.likes.length} Likes</p>
                <p>
                  <span style={{ fontWeight: "bold" }}>
                    {element.postedBy.userName}
                  </span>{" "}
                  : {element.body}
                </p>
              </div>
              <div className="card-comment">
                <input
                  type="text"
                  id="${element._id} input"
                  placeholder="Type a comment"
                  value={comment}
                  onChange={(e) => {
                    setcomment(e.target.value);
                  }}
                />
                <button
                  className="comment-btn"
                  onClick={() => {
                    handlecomment(element._id);
                  }}
                >
                  Post
                </button>
              </div>
              <div className="comment-section">
                <p>
                  {element.comments.length} comments
                </p>
                <p>
                <span>
                    Click to show comments
                  </span>
                </p>

              </div>
            </div>
          );
        })}
          <div className="show-comment">
            <div className="container">
              <div className="sc-image">
                <img src="https://images.pexels.com/photos/15422042/pexels-photo-15422042/free-photo-of-black-and-white-fashion-man-people.jpeg?auto=compress&cs=tinysrgb&w=800" alt="" />
              </div>
              <div className="sc-content">
              <div className="card-header">
                <div className="card-pic">
                  <img
                    src="https://images.pexels.com/photos/15422042/pexels-photo-15422042/free-photo-of-black-and-white-fashion-man-people.jpeg?auto=compress&cs=tinysrgb&w=800"
                    alt=""
                  />
                </div>
                <h3>Anubhav goel</h3>
              </div>
              <div className="sc-comments">
              <p>
                  <span style={{ fontWeight: "bold" }}>
                    anubhav_04
                  </span>{" "}
                  : hehehehehe
                </p>
              </div>
              <div className="card-content">
              {!item.likes.includes(user._id) ? (
                  <span
                    className="material-symbols-outlined"
                    onClick={() => {
                      likepost(item._id);
                    }}
                  >
                    favorite
                  </span>
                ) : (
                  <span
                    className="material-symbols-outlined red"
                    onClick={() => {
                      unlikepost(item._id);
                    }}
                  >
                    favorite
                  </span>
                )}
                <p>{item.likes.length} Likes</p>
                <p>
                  <span style={{ fontWeight: "bold" }}>
                    {item.postedBy.userName}
                  </span>{" "}
                  : {item.body}
                </p>
              </div>
              </div>
            </div>
          </div>
      </div>
    </>
  );
};

export default Home;
