import React, { useState, useEffect } from "react";
import Navbar from "./NavBar";
import "./Profile.css";
import Postdetails from "./Postdetails";

const Profile = () => {
  const [user, setuser] = useState({});
  const [myposts, setmyposts] = useState([]);
  const [popuppost, setpopuppost] = useState(null);
  const [show, setshow] = useState(false);
  useEffect(() => {
    getdetails();
    fetchmyposts();
  }, []);
  const fetchmyposts = async () => {
    const res = await fetch("http://localhost:2000/myposts", {
      method: "get",
      headers: {
        "auth-token": localStorage.getItem("token"),
      },
    });
    const parsed = await res.json();
    setmyposts(parsed);
  };
  const getdetails = async () => {
    const res = await fetch("http://localhost:2000/fetchdetails", {
      method: "get",
      headers: {
        "auth-token": localStorage.getItem("token"),
      },
    });
    const parsed = await res.json();
    setuser(parsed.user);
  };

  const deletepost = async(postId)=>{
    const res = await fetch(`http://localhost:2000/deletepost/${postId}`,{
      method:"delete",
      headers:{
        "auth-token":localStorage.getItem('token')
      },
      body:JSON.stringify({postId})
    })
    const parsed = await res.json();
    if(parsed)
    fetchmyposts();
  };

  const toggle = async (element)=>{
    if(show){
      setshow(false);
      setpopuppost(null);
    }
    else{
      setpopuppost(element);
      setshow(true);
    }
  }
  const childClick = (e) => {
    e.stopPropagation();
  };
  return (
    <>
      <Navbar />
      <div className="profile">
        <div className="profile-top">
          <div className="profile-pic">
            <img
              src="https://images.pexels.com/photos/15422042/pexels-photo-15422042/free-photo-of-black-and-white-fashion-man-people.jpeg?auto=compress&cs=tinysrgb&w=800"
              alt=""
            />
          </div>
          <div className="profile-data">
            <h2>anubhav_04</h2>
            <h3>Anubhav Goel</h3>
            <p>{myposts.length} Posts</p>
            <p>100 Followers</p>
            <p>100 Following</p>
          </div>
        </div>
        <hr style={{ opacity: "0.7", margin: "10px auto" }}></hr>
        <div className="gallery">
          {myposts.map((element) => {
            return (
              <div onClick={()=>{
                toggle(element)
              }} key={element._id}style={{position:"relative"}}> 
                <img src={element.image} alt="" key={element.image} />
              </div>
            );
          })}
        </div>
      </div>
      {show&&<Postdetails popupPost={popuppost} setpopupPost={setpopuppost} show ={show} setshow={setshow} fetchmyposts={fetchmyposts} user={user}/>}
    </>
  );
};

export default Profile;
