import React ,{useState} from "react";
import Navbar from "./NavBar";
import preview from "../img/preview.png";
import "./Createpost.css";
import { toast} from 'react-toastify';


const Createpost = () => {

  notifysuccess = (msg) => toast.success(msg);
  const notifyerror = (error) => toast.error(error);

  const [image, setimage] = useState("")
  const [body, setBody] = useState("")

  const loadfile = (event) => {
    var output = document.getElementById("output");
    output.src = URL.createObjectURL(event.target.files[0]);
    output.onload = function () {
      URL.revokeObjectURL(output.src); // free memory
    }
  }
  const postdata = async ()=>{
    const data = new FormData();
    data.append("file",image);
    data.append("upload_preset","instagram-clone");
    data.append("cloud_name","anubhavcloudinary");
    const res = await fetch("https://api.cloudinary.com/v1_1/anubhavcloudinary/image/upload",{
      method:"post",
      body:data
    })
    const parsed = await res.json();
    postToMongo(parsed.url);
  }
  const postToMongo= async (url)=>{
    try{
     const res = await fetch("http://192.168.29.163:2000/createpost",{
      method:"post",
       headers:{
         "Content-Type":"application/json",
         "auth-token":localStorage.getItem("token")
       },
       body:JSON.stringify({image:url,body})
     })
     const parsed = await res.json();
    }
    catch(error)
    {
      notifyerror(error); 
    }



  }
  return (
    <>
      <Navbar />
      <div className="post">
        <div className="post-header">
          <h3>Create Post</h3>
          <div className="post-btn">
            <button id="btn" onClick={postdata}>Post</button>
          </div>
        </div>
        <div className="post-input">
          <input
            type="file"
            accept="image/*"
            onChange={(event) => {
              loadfile(event);
              setimage(event.target.files[0])
            }}
          />
          <img id="output" src={preview} alt=""/>
        </div>
        <div className="details">
          <div className="prof-pic">
            <img
              src="https://images.pexels.com/photos/15422042/pexels-photo-15422042/free-photo-of-black-and-white-fashion-man-people.jpeg?auto=compress&cs=tinysrgb&w=800"
              alt=""
            />
          </div>
          <div style={{width:"100%"}}>
          <h4>anubhav_04</h4>
          <textarea placeholder="Enter a suitable caption..." value={body} onChange={(e)=>{setBody(e.target.value)}}></textarea>
          </div>
        </div>
      </div>
    </>
  );
};

export default Createpost;
